window.addEventListener("load",(()=>{let e=0;const t=[];$.getJSON(document.querySelector("#changelog-info").getAttribute("changelog-url"),(function(n){for(const o of n){e++;const n=[];for(const e of o.changes)n.push(`<li><p>${e}</p></li>`);const r=`<div class="card bg-body-secondary">\n                  <div class="card-header rounded-top bg-body-tertiary">\n                     Version ${o.version}\n                  </div>\n                  <div class="card-body rounded-bottom">\n                     <h5 class="card-title fw-bold">Changes:</h5>\n                     <p class="card-text">\n                        <ul>\n                           ${n.join("")}\n                        </ul>\n                     </p>\n                  </div>\n               </div><br>`;1==e?document.querySelector("#currentVersion").insertAdjacentHTML("afterbegin",r):2==e?document.querySelector("#previousVersion").insertAdjacentHTML("afterbegin",r):t.push(r)}document.querySelector("#pastVersion").insertAdjacentHTML("afterbegin",t.join(""))})),document.querySelector("#moreListButton").addEventListener("click",(()=>{const e=document.querySelector("#moreListButton");document.querySelector("#moreList").classList.toggle("d-none")?(e.textContent="Show More",e.classList.add("mb-2")):(e.textContent="Show Less",e.classList.remove("mb-2"))}))}));